import React, { Component } from 'react'
import './Conversor.css'

class Conversor extends Component {
  constructor (props) {
    super(props)

    this.state = {
      moedaA_valor: '',
      moedaB_valor: 0,
      moedaA_valorStr: '',
      moedaB_valorStr: 0
    }
  }

  convert = () => {
    let from_to = `${this.props.moedaA}_${this.props.moedaB}`
    let url = `https://free.currconv.com/api/v7/convert?apiKey=02a0baf4414a654a31db&q=${from_to}&compact=y`

    fetch(url)
      .then(res => {
        return res.json()
      })
      .then(json => {
        let cot = json[from_to].val
        let moedaB_valorStr = parseFloat(this.state.moedaA_valorStr * cot)
        this.setState({ moedaB_valorStr })
      })
  }

  maskValue = i => {
    var v = i.replace(/\D/g, '')
    v = (v / 100).toFixed(2) + ''
    v = v.replace('.', ',')
    v = v.replace(/(\d)(\d{3})(\d{3}),/g, '$1.$2.$3,')
    v = v.replace(/(\d)(\d{3}),/g, '$1.$2,')
    return v
  }

  setVal = () => {
    let val = this.maskValue(this.state.moedaA_valorStr)
    return val
  }

  setRes = () => {
    console.log(this.state.moedaB_valorStr)
    let val
    if (this.state.moedaB_valorStr != 0) {
      val = this.maskValue(this.state.moedaB_valorStr)
    } else {
      val = this.state.moedaB_valorStr
    }
    //let val = this.state.moedaB_valorStr
    return val.toFixed(2)
  }

  render () {
    return (
      <div className='conversor'>
        <div className='title2'>
          <h2>
            {this.props.moedaA} -> {this.props.moedaB}
          </h2>
        </div>
        <div className='inputs'>
          <input
            className='input'
            type='text'
            //value={this.setVal()}
            value={this.state.moedaA_valorStr}
            onChange={event => {
              event.persist()
              let ev = event
              var val = event.target.value.replace(',', '.')
              this.setState({ moedaA_valorStr: val })
            }}
          ></input>
          <input
            className='btn'
            type='button'
            value='Convert'
            onClick={this.convert}
          ></input>
          <input
            className='result'
            //value={this.setRes()}
            value={this.state.moedaB_valorStr.toFixed(2)}
            disabled={true}
          ></input>
        </div>
      </div>
    )
  }
}

export default Conversor
