import React, { Component } from 'react'
import './Conversor.css'

class Conversor extends Component {
  constructor (props) {
    super(props)

    this.state = {
      moedaA_valor: '',
      moedaB_valor: ''
    }
  }

  convert = () => {
    let from_to = `${this.props.moedaA}_${this.props.moedaB}`
    let url = `https://free.currconv.com/api/v7/convert?apiKey=02a0baf4414a654a31db&q=${from_to}&compact=y`

    fetch(url)
      .then(res => {
        return res.json()
      })
      .then(json => {
        let cot = json[from_to].val
        let moedaB_valor = parseFloat(
          (this.state.moedaA_valor * cot)
        )
        this.setState({ moedaB_valor })
      })
  }

  maskValue = (i) =>{
    var v = i.replace(/\D/g,'');
    v = (v/100).toFixed(2) + '';
    v = v.replace(".", ",");
    v = v.replace(/(\d)(\d{3})(\d{3}),/g, "$1.$2.$3,");
    v = v.replace(/(\d)(\d{3}),/g, "$1.$2,");
    return v
  }

  setVal = () => {
    let val = this.maskValue(this.state.moedaA_valor)
    return val
  }

  setRes = () => {
    let val = this.maskValue(this.state.moedaB_valor)
    return val
  }

  render () {
    return (
      <div className='conversor'>
        <div className='title2'>
          <h2>
            {this.props.moedaA} -> {this.props.moedaB}
          </h2>
        </div>
        <div className="inputs">
          <input
            className='input'
            type='text'
            value={this.setVal()}
            onChange={event => {
              event.persist()
              let ev = event
              var val = event.target.value.replace(',', '.')
              this.setState({ moedaA_valor: val })
            }}
          ></input>
          <input
            className='btn'
            type='button'
            value='Convert'
            onClick={this.convert}
          ></input>
          <input
            className='result'
            value={this.setRes()}
            disabled={true}
          ></input>
        </div>
      </div>
    )
  }
}

export default Conversor
